mapper.select:
SELECT DISTINCT sct.id,s.nickname,pr.practice_way AS practiceWay , pr.risk,pr.healthy, s.name,s.phone,sct.score,c.start_stime AS startStime,s.email AS sEmail,t.email AS tEmail,c.`name` As cname,c.end_etime AS endEtime,t.`name` AS tname,t.phone AS tphone from t_student s , t_student_course_teacher sct, t_course c, t_teacher t, t_practice_risk pr where s.id = sct.student_id
AND s.name like CONCAT('%',#{name},'%')
AND t.name = #{tname}
AND s.major_id = #{secMajorId}
and t.id = sct.teacher_id and c.id = sct.course_id and sct.id = pr.id

SELECT DISTINCT sct.id,s.nickname,pr.practice_way AS practiceWay , pr.risk,pr.healthy, dic.dict_value AS TeacherPost, tc.company_name As companyName ,tc.students_post AS studentsPost, tc.out_supervisor AS outsupervisor,tc.supervisor_post AS supervisorPost,tc.outor_phone AS outorPhone, s.name,s.phone,sct.score,c.start_stime AS startStime,s.email AS sEmail,t.email AS tEmail,c.`name` As cname,c.end_etime AS endEtime,t.`name` AS tname,t.phone AS tphone from t_student s , t_student_course_teacher sct, t_course c, t_teacher t, t_practice_risk pr, t_company tc, t_sys_dict dic where s.id = sct.student_id
AND s.name like CONCAT('%',#{name},'%')
AND t.name = #{tname}
AND s.major_id = #{secMajorId}
and t.id = sct.teacher_id and c.id = sct.course_id and sct.id = pr.id and tc.studnet_id = sct.student_id and tc.course_id = sct.course_id and dic.dict_code = t.title
